package com.cg.fandamental;

public class IfElseStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=12;
		if(x+y <10) {
			System.out.println("x+y is less than 10");
			
		}
		else {
			System.out.println("x+y is greater than 10");
		}

	}

}
